import React, {Component} from 'react';
import ImgList from './ImgList';
import axios from 'axios';

class Container extends Component {

  constructor(props){
    super(props)
    this.state = {
      apiKey: "be62b8fd6260c4f8f8b4f845623d0257",
      loading: true,
      imgs: [],
      query: this.props.query
    }
  }

  performSearch = (query='Ice Castle') => {
    console.log(`http://localhost:5000/?text=${query}`);
    // axios.get(`http://localhost:5000/?text=${query}`)
    //   .then(response => {
    //     this.setState({loading:true});
    //     setTimeout(() => {
    //       this.setState({
    //         query: query,
    //         imgs: response.data.photos.photo,
    //         loading: false
    //       })
    //     }, 150);
    //   })
    //   .catch(error => {
    //     console.log('Error fetching and parsing data', error);
    //   })
    fetch(`http://localhost:5000/?text=${query}`, {
      method: 'GET',
      headers:{'content-type': 'application/json'},
      mode: 'no-cors'
    })
    .then((response) => {
      this.setState({loading:true});
        setTimeout(() => {
          this.setState({
            query: query,
            imgs: response.rs.photos.photo,
            loading: false
          })
        }, 150);
    }   
    )
    .catch((error) => {
      console.log('Error fetching and parsing data', error);
    });
  }

  componentWillReceiveProps(props){
    (props.query)
      ? this.performSearch(props.query)
      : this.performSearch(props.match.params.query)
  }

  componentWillMount(){
    this.performSearch()
  }

  render() {
    return(
      <div className="photo-container">  
        <h2>{this.state.query}</h2>
        {(this.state.loading) ? <p>Loading...</p> : 
        <ImgList data={this.state.imgs} /> }
      </div>
    )
  } 
}


export default Container;
